#include <iostream>
//Include for including Libraries
//Istream is a library for Functions
// This is a Comment 
int main()
{
 int price = 500; 
 //This a variable 
 std::cout << "The Price is " << price; 
 // Before Adding The Varible Put <<  
 std::cout << "\nThe new Price is "<< price * 9999999999;

 std::cout << "\nThe Price for My Friend is " << price / 9999999999;

 price = 300; 
 //Update the Variable without int
 std::cout << "\nThe Updated Price Is " << price + 500;
 return 0;
}
